// Your name to search for (CUSTOMIZE THIS)
const YOUR_NAME = 'John Doe'; // Replace with your actual name
const YOUR_NAME_VARIATIONS = [
  'John Doe',
  'JOHN DOE', 
  'john doe',
  'J. Doe',
  'John D.',
  'Doe, John'
]; // Add variations of your name

// Get extracted data
const extractedData = $input.all()[0]?.json?.output || {};

// Combine all text sources to search
let searchText = '';
if (extractedData.student_list) {
  searchText += JSON.stringify(extractedData.student_list) + ' ';
}
if (extractedData.selected_students) {
  searchText += JSON.stringify(extractedData.selected_students) + ' ';
}

// Also search in original text content
const originalText = $input.all()[0]?.json?.text || '';
searchText += originalText;

// Search for name variations
let nameFound = false;
let foundVariation = '';

for (const nameVar of YOUR_NAME_VARIATIONS) {
  if (searchText.toLowerCase().includes(nameVar.toLowerCase())) {
    nameFound = true;
    foundVariation = nameVar;
    break;
  }
}

// Determine selection status
let selectionStatus = 'NOT_FOUND';
if (nameFound) {
  // Check if it's in a selection context
  if (searchText.toLowerCase().includes('selected') || 
      searchText.toLowerCase().includes('shortlisted') ||
      searchText.toLowerCase().includes('qualified') ||
      searchText.toLowerCase().includes('eligible')) {
    selectionStatus = 'SELECTED';
  } else if (searchText.toLowerCase().includes('rejected') ||
             searchText.toLowerCase().includes('not selected') ||
             searchText.toLowerCase().includes('unsuccessful')) {
    selectionStatus = 'NOT_SELECTED';
  } else {
    selectionStatus = 'MENTIONED';
  }
}

return {
  ...extractedData,
  name_search: {
    name_found: nameFound,
    found_variation: foundVariation,
    selection_status: selectionStatus,
    searched_name: YOUR_NAME
  }
};

// Get email data and attachment analysis
const emailData = $input.first()?.json?.output || {};
const attachmentData = $input.all().slice(1) || [];

// Merge all company information
let mergedData = {
  company_name: emailData.company_name || 'Unknown Company',
  job_roles: [...(emailData.job_roles || [])],
  requirements: [...(emailData.requirements || [])],
  deadlines: [...(emailData.deadlines || [])],
  event_dates: [...(emailData.event_dates || [])],
  selection_status: emailData.selection_status || '',
  contact_info: emailData.contact_info || '',
  email_info: {
    subject: $('Gmail Trigger - Placement Emails').item.json.subject,
    from: $('Gmail Trigger - Placement Emails').item.json.from,
    date: $('Gmail Trigger - Placement Emails').item.json.date,
    snippet: $('Gmail Trigger - Placement Emails').item.json.snippet
  },
  attachments_processed: attachmentData.length,
  name_search_results: []
};

// Process attachment data
attachmentData.forEach((attachment, index) => {
  if (attachment.json) {
    const data = attachment.json;

    // Merge company names (prefer non-empty ones)
    if (data.company_name && data.company_name !== 'Unknown Company') {
      mergedData.company_name = data.company_name;
    }

    // Merge arrays
    if (data.job_roles) mergedData.job_roles.push(...data.job_roles);
    if (data.requirements) mergedData.requirements.push(...data.requirements);
    if (data.event_dates) mergedData.event_dates.push(...data.event_dates);

    // Collect name search results
    if (data.name_search) {
      mergedData.name_search_results.push(data.name_search);
    }
  }
});

// Remove duplicates from arrays
mergedData.job_roles = [...new Set(mergedData.job_roles)];
mergedData.requirements = [...new Set(mergedData.requirements)];
mergedData.deadlines = [...new Set(mergedData.deadlines)];
mergedData.event_dates = [...new Set(mergedData.event_dates)];

// Determine overall selection status
let overallSelectionStatus = 'NOT_FOUND';
let selectedInAny = false;

mergedData.name_search_results.forEach(result => {
  if (result.selection_status === 'SELECTED') {
    selectedInAny = true;
    overallSelectionStatus = 'SELECTED';
  } else if (result.selection_status === 'NOT_SELECTED' && !selectedInAny) {
    overallSelectionStatus = 'NOT_SELECTED';
  } else if (result.selection_status === 'MENTIONED' && overallSelectionStatus === 'NOT_FOUND') {
    overallSelectionStatus = 'MENTIONED';
  }
});

mergedData.overall_selection_status = overallSelectionStatus;

return mergedData;

// Process event dates and create calendar event data
const eventDates = $json.event_dates || [];
const companyName = $json.company_name || 'Unknown Company';
const jobRoles = $json.job_roles || [];

const calendarEvents = [];

eventDates.forEach((eventStr, index) => {
  // Try to parse date from string
  let eventDate;
  let eventTitle = `${companyName} - Placement Event`;

  // Common date patterns to look for
  const datePatterns = [
    /\d{1,2}[-/]\d{1,2}[-/]\d{4}/,  // DD-MM-YYYY or MM-DD-YYYY
    /\d{4}[-/]\d{1,2}[-/]\d{1,2}/,  // YYYY-MM-DD
    /\d{1,2}\s+(Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)\s+\d{4}/i, // DD Month YYYY
    /(Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)\s+\d{1,2},?\s+\d{4}/i // Month DD, YYYY
  ];

  let dateMatch = null;
  for (const pattern of datePatterns) {
    dateMatch = eventStr.match(pattern);
    if (dateMatch) break;
  }

  if (dateMatch) {
    try {
      eventDate = new Date(dateMatch[0]);
      if (isNaN(eventDate.getTime())) {
        // If parsing failed, set to tomorrow
        eventDate = new Date();
        eventDate.setDate(eventDate.getDate() + 1);
      }
    } catch (e) {
      // Default to tomorrow if parsing fails
      eventDate = new Date();
      eventDate.setDate(eventDate.getDate() + 1);
    }
  } else {
    // If no date pattern found, set to tomorrow
    eventDate = new Date();
    eventDate.setDate(eventDate.getDate() + 1);
  }

  // Determine event type and title
  if (eventStr.toLowerCase().includes('interview')) {
    eventTitle = `${companyName} - Interview`;
  } else if (eventStr.toLowerCase().includes('test') || eventStr.toLowerCase().includes('assessment')) {
    eventTitle = `${companyName} - Assessment/Test`;
  } else if (eventStr.toLowerCase().includes('drive') || eventStr.toLowerCase().includes('campus')) {
    eventTitle = `${companyName} - Campus Drive`;
  } else if (jobRoles.length > 0) {
    eventTitle = `${companyName} - ${jobRoles[0]} Event`;
  }

  // Set start time to 9 AM and end time to 10 AM (1 hour duration)
  const startTime = new Date(eventDate);
  startTime.setHours(9, 0, 0, 0);

  const endTime = new Date(eventDate);
  endTime.setHours(10, 0, 0, 0);

  calendarEvents.push({
    title: eventTitle,
    start: startTime.toISOString(),
    end: endTime.toISOString(),
    description: `Event Details: ${eventStr}\n\nCompany: ${companyName}\nJob Roles: ${jobRoles.join(', ')}\nEmail Subject: ${$json.email_info?.subject || ''}\n\nSelection Status: ${$json.overall_selection_status}`,
    originalEventString: eventStr
  });
});

return {
  ...$json,
  calendar_events: calendarEvents
};

