// Format comprehensive company summary
const data = $json;

// Helper function to format arrays
function formatList(items, emptyText = 'Not specified') {
  if (!items || items.length === 0) return emptyText;
  return items.map(item => `â€¢ ${item}`).join('\n');
}

// Helper function to format selection status
function formatSelectionStatus(status, searchResults) {
  let statusEmoji = 'â“';
  let statusText = 'Status unknown';

  switch (status) {
    case 'SELECTED':
      statusEmoji = 'âœ…';
      statusText = 'YOU ARE SELECTED!';
      break;
    case 'NOT_SELECTED':
      statusEmoji = 'âŒ';
      statusText = 'NOT SELECTED';
      break;
    case 'MENTIONED':
      statusEmoji = 'ğŸ“';
      statusText = 'YOUR NAME WAS MENTIONED';
      break;
    default:
      statusEmoji = 'ğŸ“‹';
      statusText = 'NO SELECTION INFO FOUND';
  }

  let searchInfo = '';
  if (searchResults && searchResults.length > 0) {
    const foundResult = searchResults.find(r => r.name_found);
    if (foundResult) {
      searchInfo = `\nSearched for: ${foundResult.searched_name}\nFound as: ${foundResult.found_variation}`;
    }
  }

  return `${statusEmoji} ${statusText}${searchInfo}`;
}

// Format the complete message
const message = `ğŸ“‹ **PLACEMENT UPDATE**

ğŸ¢ **Company:** ${data.company_name}

ğŸ’¼ **Roles:**
${formatList(data.job_roles)}

ğŸ“‹ **Requirements:**
${formatList(data.requirements)}

â° **Deadlines:**
${formatList(data.deadlines)}

ğŸ“… **Event Dates:**
${formatList(data.event_dates)}

ğŸ“§ **Email Details:**
â€¢ Subject: ${data.email_info?.subject || 'N/A'}
â€¢ From: ${data.email_info?.from || 'N/A'}
â€¢ Date: ${data.email_info?.date || 'N/A'}

ğŸ¯ **SELECTION STATUS:**
${formatSelectionStatus(data.overall_selection_status, data.name_search_results)}

ğŸ“ **Attachments Processed:** ${data.attachments_processed || 0}

${data.contact_info ? `ğŸ“ **Contact:** ${data.contact_info}\n` : ''}
#PlacementUpdate #${data.company_name.replace(/\s+/g, '')}`;

return {
  telegram_message: message,
  ...data
};
